import React, { useState, useEffect } from 'react';
import { Paper, Typography } from '@mui/material';
import { styled } from '@mui/system';

const PlanetCardContainer = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  backgroundColor: '#f9f5e3',
  borderRadius: theme.spacing(1),
  height: '300px',
  overflow: 'auto',
  transition: 'transform 0.3s ease-in-out', // Add transition for smoother hover effect
  '&:hover': {
    transform: 'scale(1.05)', // Increase scale on hover for a subtle zoom effect
  },
  '&::-webkit-scrollbar': {
    width: '0px',  // Hide scrollbar for Webkit browsers (Chrome, Safari)
    background: 'transparent',  // Optional: set background color of the scrollbar track
  },
  scrollbarWidth: 'none',  // Hide scrollbar for Firefox
}));

const ResidentsList = styled('div')({
  marginTop: '16px',
  paddingLeft: '16px',
});

const PlanetCard = ({ planet }) => {
  const [residents, setResidents] = useState([]);

  useEffect(() => {
    const fetchResidents = async () => {
      try {
        const residentsData = await Promise.all(
          planet.residents.map(async (residentUrl) => {
            const response = await fetch(residentUrl);
            return await response.json();
          })
        );
        setResidents(residentsData);
      } catch (error) {
        console.error('Error fetching residents:', error);
      }
    };

    fetchResidents();
  }, [planet.residents]);

  // Define color mapping based on the name of the planet
  const getColor = (name) => {
    const colorCodes = ['#ff0000', '#ff7f00', '#0000ff', '#00ff00']; // Add more color codes if needed
    const index = name.charCodeAt(0) % colorCodes.length;
    return colorCodes[index];
  };

  return (
    <PlanetCardContainer elevation={3}>
      <Typography variant="h5" component="h2" gutterBottom style={{ color: getColor(planet.name) }}>
        {planet.name}
      </Typography>
      <Typography variant="body2" color="textSecondary" gutterBottom style={{ color: getColor(planet.name) }}>
        Climate: {planet.climate}
      </Typography>
      <Typography variant="body2" color="textSecondary" gutterBottom style={{ color: getColor(planet.name) }}>
        Population: {planet.population}
      </Typography>
      <Typography variant="body2" color="textSecondary" gutterBottom style={{ color: getColor(planet.name) }}>
        Terrain: {planet.terrain}
      </Typography>
      <ResidentsList>
        <Typography variant="body2" color="textSecondary" gutterBottom style={{ color: getColor(planet.name) }}>
          Residents:
        </Typography>
        <ul>
          {residents.map((resident, index) => (
            <li key={index}>{resident.name}</li>
          ))}
        </ul>
      </ResidentsList>
    </PlanetCardContainer>
  );
};

export default PlanetCard;
