import React, { useState, useEffect } from 'react';
import { Paper, Typography } from '@mui/material';
import { styled } from '@mui/system';

const PlanetCardContainer = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  backgroundColor: '#f5f5f5',
  borderRadius: theme.spacing(1),
  height: '200px', // Reduced card height
  overflow: 'hidden', // Hide overflow content
  transition: 'transform 0.3s ease-in-out',
  '&:hover': {
    transform: 'scale(1.05)',
  },
}));

const ResidentsList = styled('div')({
  marginTop: '16px',
  paddingLeft: '16px',
});

const PlanetCard = ({ planet }) => {
  const [residents, setResidents] = useState([]);

  useEffect(() => {
    const fetchResidents = async () => {
      try {
        const residentsData = await Promise.all(
          planet.residents.map(async (residentUrl) => {
            const response = await fetch(residentUrl);
            return await response.json();
          })
        );
        setResidents(residentsData);
      } catch (error) {
        console.error('Error fetching residents:', error);
      }
    };

    fetchResidents();
  }, [planet.residents]);

  return (
    <PlanetCardContainer elevation={3}>
      <Typography variant="h5" component="h2" gutterBottom>
        {planet.name}
      </Typography>
      <Typography variant="body2" color="textSecondary" gutterBottom>
        Climate: {planet.climate}
      </Typography>
      <Typography variant="body2" color="textSecondary" gutterBottom>
        Population: {planet.population}
      </Typography>
      <Typography variant="body2" color="textSecondary" gutterBottom>
        Terrain: {planet.terrain}
      </Typography>
      <ResidentsList>
        <Typography variant="body2" color="textSecondary" gutterBottom>
          Residents:
        </Typography>
        <ul>
          {residents.map((resident, index) => (
            <li key={index}>{resident.name}</li>
          ))}
        </ul>
      </ResidentsList>
    </PlanetCardContainer>
  );
};

export default PlanetCard;
