import React from 'react';
import { Button } from '@mui/material';
import { makeStyles } from '@mui/styles'; // Import makeStyles from @mui/styles

const useStyles = makeStyles((theme) => ({
  paginationContainer: {
    textAlign: 'center',
    marginTop: theme.spacing(2), // Add spacing from the top
  },
  paginationButton: {
    margin: theme.spacing(1), // Add spacing between buttons
    backgroundColor: '#1976d2', // Custom button color
    color: '#ffffff', // White text color
    '&:hover': {
      backgroundColor: '#1565c0', // Darker shade on hover
    },
  },
}));

const Pagination = ({ goToNextPage, goToPrevPage }) => {
  const classes = useStyles();

  return (
    <div className={classes.paginationContainer}>
      {goToPrevPage && (
        <Button onClick={goToPrevPage} variant="contained" className={classes.paginationButton}>
          Previous
        </Button>
      )}
      {goToNextPage && (
        <Button onClick={goToNextPage} variant="contained" className={classes.paginationButton}>
          Next
        </Button>
      )}
    </div>
  );
};

export default Pagination;
